mapper.select:
SELECT post_no, subject, hate_count, like_count, e_mail, msg, post_date 
FROM post
WHERE post_no = #{post_no}

SELECT post_no, img, video
FROM media_tag
WHERE post_no = #{post_no}

mapper.update:
		UPDATE post
		SET like_count = #{like_count}
    	WHERE post_no = #{post_no}

		UPDATE post
		SET hate_count = #{hate_count}
    	WHERE post_no = #{post_no}

		UPDATE reply
		SET like_count = #{like_count}
    	WHERE reply_no = #{reply_no} AND post_no = #{post_no}

mapper.select:
SELECT like_count
FROM reply
WHERE reply_no = #{reply_no} AND post_no = #{post_no}

SELECT COUNT(*)
FROM report
WHERE reported_email = #{reported_email}

mapper.update:
UPDATE member
SET bl_flag = 'T'
WHERE e_mail = #{e_mail}
